<script>
export default {
    head() {
        return {
            bodyAttrs: {
                class: this.computedClass
            }
        };
    },
    data() {
        return {
            nav_drawer: this.$refs["mobile-nav"],
            class: "light"
        };
    },
    computed: {
        computedClass() {
            return this.class;
        }
    },
    methods: {
        openMenu() {
            this.$refs["mobile-nav"].style.left = "0%";
        },

        closeMenu() {
            this.$refs["mobile-nav"].style.left = "-100%";
        }
    },
    mounted() {
        this.$store.commit("showTab");
        this.$store.dispatch("setCurrentPage");
        let btn_body = this.$refs["btn-body"];
        let theme_help_text = this.$refs["theme-help-text"];
        btn_body.addEventListener("click", () => {
            if (btn_body.dataset.theme == "dark") {
                btn_body.setAttribute("style", "justify-content: flex-start");
                btn_body.dataset.theme = "light";
                theme_help_text.innerText = "Switch to Dark Mode";
                document.body.classList.replace("dark", "light");
                this.class = "light";
            } else {
                theme_help_text.innerText = "Switch to Light Mode";
                btn_body.setAttribute("style", "justify-content: flex-end");
                btn_body.dataset.theme = "dark";
                document.body.classList.replace("light", "dark");
                this.class = "dark";
            }
        });
    },
    beforeDestroy() {
        this.$store.commit("hideTab");
    }
};
</script>

